mapper.select:
select * from
	(select rownum as num, sorted.* from
		(select class.*,nickname from class join member on (creater_id = email) 

			where category1=#{category1} 

		order by reg_date desc)sorted )
where num between ${startNum} and ${endNum}

select * from (select rownum as num, sorted.* from (select class.*,nickname from class join member on (creater_id = email) where title like '%'||#{searchContents}||'%'
and category1=#{category1}
order by reg_date desc)sorted ) where num between ${startNum} and ${endNum}
