mapper.insert:
insert into community 
	values(
			${board_seq}.nextval
			,#{title}
			,#{writer}
			,#{contents}
			,default
			,default
			,default
			,#{hash_tag}
			,default
			,default
		 )


	select ${board_seq}.currval from dual

mapper.select:

select * from 
				(select row_number() over(order by write_date desc) line, community.* 
				from community



						substr(board_seq,1,1) = #{category}


						and title like '%'||#{searchContent}||'%'
						and writer like '%'||#{searchContent}||'%'
						and contents like '%'||#{searchContent}||'%'





				)
		where line between #{start} and #{end}

mapper.insert:
