mapper.insert:
insert into community 
	values(
			${board_seq}.nextval
			,#{title}
			,#{writer}
			,#{contents}
			,default
			,default
			,default
			,#{hash_tag}
			,default
			,default
		 )


	select ${board_seq}.currval from dual

mapper.select:
select * from (select row_number() over(order by write_date desc) line, community.* from community
substr(board_seq,1,1) like '%'||#{category}
substr(board_seq,1,1) like '%'||#{category} and title like '%'||#{searchContent}||'%' or substr(board_seq,1,1) like '%'||#{category} and writer like '%'||#{searchContent}||'%' or substr(board_seq,1,1) like '%'||#{category} and contents like '%'||#{searchContent}||'%'
hash_tag like '%'||#{searchContent}||'%'
) where line between #{start} and #{end} and state != '2'

mapper.update:
update community 
		set title = #{title}
					,contents = #{contents}
					,hash_tag = #{hash_tag}
		where board_seq = #{board_seq}

update community 
		set like_count = 


						like_count+1


						like_count-1


		where board_seq = #{seq}


	select like_count from community where board_seq = #{seq}

