mapper.select:
select * from (select c.*, r.reg_date "buy_date", row_number() over (order by r.regstds_seq desc) rn from class c
inner join class_RegStds r on (c.class_seq = r.parent_seq) where std_id = #{email} and state = 0)

	where rn between #{start} and #{end}

select * from (select c.*, row_number() over (order by g.like_seq desc) rn from class c left join good g on (c.class_seq = g.parent_seq)
where g.email = #{email})

	where rn between #{start} and #{end}

select * from (select r.*, c.category1, c.title, c.creater_info, row_number() over (order by r.review_seq desc) rn from class_review r
inner join class c on (r.parent_seq = c.class_seq) where r.std_id = #{email} and r.state = 0)

	where rn between #{start} and #{end}

select * from (select r.board_seq, r.contents, r.like_count, TO_CHAR(c.write_date, 'YYYY/MM/DD HH24:mi:ss') "write_date", c.title, row_number() over (order by r.write_date desc) rn from reply r
inner join community c on (r.board_seq = c.board_seq) where r.writer = #{email} and r.state = 0)

	where rn between #{start} and #{end}
